import { useEffect } from "react";

function Search({ query, setQuery }) {
  useEffect();
  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

export default Search;
